mapper.select:
SELECT id, article_name, article_outline, image_desc, article_content, voice_tone, voice_link, voice_file_path, video_link, video_file_path, publish_status, create_time, update_time FROM article
res_state = 1
AND article_name LIKE CONCAT('%', #{articleName,jdbcType=VARCHAR}, '%')
AND voice_tone = #{voiceTone,jdbcType=VARCHAR}
AND publish_status = #{publishStatus,jdbcType=SMALLINT}
ORDER BY create_time DESC

mapper.insert:
INSERT INTO article (
    article_name, article_outline, image_desc, article_content, voice_tone, voice_link, voice_file_path,
    video_link, video_file_path, publish_status, res_state, create_time, update_time
) VALUES (
    #{articleName,jdbcType=VARCHAR}, #{articleOutline,jdbcType=VARCHAR}, #{imageDesc,jdbcType=VARCHAR}, #{articleContent,jdbcType=VARCHAR},
    #{voiceTone,jdbcType=VARCHAR}, #{voiceLink,jdbcType=VARCHAR}, 
    #{voiceFilePath,jdbcType=VARCHAR}, #{videoLink,jdbcType=VARCHAR}, 
    #{videoFilePath,jdbcType=VARCHAR}, #{publishStatus,jdbcType=SMALLINT}, 
    #{resState,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
)

mapper.update:
UPDATE article
article_name = #{articleName,jdbcType=VARCHAR}, article_outline = #{articleOutline,jdbcType=VARCHAR}, image_desc = #{imageDesc,jdbcType=VARCHAR}, article_content = #{articleContent,jdbcType=VARCHAR}, voice_tone = #{voiceTone,jdbcType=VARCHAR}, voice_link = #{voiceLink,jdbcType=VARCHAR}, voice_file_path = #{voiceFilePath,jdbcType=VARCHAR}, video_link = #{videoLink,jdbcType=VARCHAR}, video_file_path = #{videoFilePath,jdbcType=VARCHAR}, publish_status = #{publishStatus,jdbcType=SMALLINT}, res_state = #{resState,jdbcType=INTEGER}, update_time = #{updateTime,jdbcType=TIMESTAMP}
WHERE id = #{id,jdbcType=BIGINT}

UPDATE article
SET res_state = 0, update_time = NOW()
WHERE id = #{id,jdbcType=BIGINT}

UPDATE article
SET publish_status = #{publishStatus,jdbcType=SMALLINT}, update_time = NOW()
WHERE id = #{id,jdbcType=BIGINT}
